import { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import JurnalForm from "./components/JurnalForm";

function App() {
  const [jurnalList, setJurnalList] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [editData, setEditData] = useState(null);

  useEffect(() => {
    const dataTersimpan = localStorage.getItem("jurnalList");
    if (dataTersimpan) {
      setJurnalList(JSON.parse(dataTersimpan));
    }
  }, []);

  useEffect(() => {
    if (jurnalList.length > 0) {
      localStorage.setItem("jurnalList", JSON.stringify(jurnalList));
    }
  }, [jurnalList]);

  const tambahJurnal = (jurnalBaru) => {
    if (editIndex !== null) {
      const jurnalBaruList = [...jurnalList];
      jurnalBaruList[editIndex] = jurnalBaru;
      setJurnalList(jurnalBaruList);
      setEditIndex(null);
      setEditData(null);
    } else {
      setJurnalList([...jurnalList, jurnalBaru]);
    }
  };

  const hapusJurnal = (index) => {
    const jurnalBaru = jurnalList.filter((_, i) => i !== index);
    setJurnalList(jurnalBaru);
    localStorage.setItem("jurnalList", JSON.stringify(jurnalBaru));
  };

  const editJurnal = (index) => {
    setEditIndex(index);
    setEditData(jurnalList[index]);
  };

  const eksporKeExcel = () => {
    const ws = XLSX.utils.json_to_sheet(jurnalList);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Jurnal Kelas");
    XLSX.writeFile(wb, "Jurnal_Kelas.xlsx");
  };

  return (
    <div>
      <h1>ðŸ“– Jurnal Kelas</h1>
      <JurnalForm tambahJurnal={tambahJurnal} editData={editData} />

      <h2>Daftar Jurnal</h2>
      {jurnalList.length === 0 ? (
        <p>Belum ada jurnal.</p>
      ) : (
        <ul>
          {jurnalList.map((jurnal, index) => (
            <li key={index}>
              <strong>{jurnal.tanggal}</strong> - {jurnal.kelas} - {jurnal.guru} - {jurnal.subjek} - {jurnal.topik} - Kehadiran: {jurnal.kehadiran} - {jurnal.keterangan}
              <button onClick={() => editJurnal(index)} style={{ marginLeft: "10px", background: "blue", color: "white" }}>Edit</button>
              <button onClick={() => hapusJurnal(index)} style={{ marginLeft: "10px", background: "red", color: "white" }}>Hapus</button>
            </li>
          ))}
        </ul>
      )}

      <button onClick={eksporKeExcel} style={{ marginTop: "20px", background: "green", color: "white", padding: "10px" }}>
        ðŸ“¥ Ekspor ke Excel
      </button>
    </div>
  );
}

export default App;
