import { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import JurnalForm from "./components/JurnalForm";
import ReactPaginate from "react-paginate";
import "tailwindcss/tailwind.css";

const ITEMS_PER_PAGE = 10;

function App() {
  const [jurnalList, setJurnalList] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [editData, setEditData] = useState(null);
  const [filterKelas, setFilterKelas] = useState("");
  const [filterBulan, setFilterBulan] = useState("");
  const [filterTahun, setFilterTahun] = useState("");
  const [filterGuru, setFilterGuru] = useState("");
  const [filterSubjek, setFilterSubjek] = useState("");
  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    const dataTersimpan = localStorage.getItem("jurnalList");
    if (dataTersimpan) {
      setJurnalList(JSON.parse(dataTersimpan));
    }
  }, []);

  useEffect(() => {
    if (jurnalList.length > 0) {
      localStorage.setItem("jurnalList", JSON.stringify(jurnalList));
    } else {
      localStorage.removeItem("jurnalList");
    }
  }, [jurnalList]);

  const tambahJurnal = (jurnalBaru) => {
    jurnalBaru.diketahui = jurnalBaru.diketahui ?? false;
    if (editIndex !== null) {
      const jurnalBaruList = [...jurnalList];
      jurnalBaruList[editIndex] = jurnalBaru;
      setJurnalList(jurnalBaruList);
      setEditIndex(null);
      setEditData(null);
    } else {
      setJurnalList([...jurnalList, jurnalBaru]);
    }
  };

  const hapusJurnal = (index) => {
    if (window.confirm("Apakah Anda yakin ingin menghapus jurnal ini?")) {
      const jurnalBaru = jurnalList.filter((_, i) => i !== index);
      setJurnalList(jurnalBaru);
    }
  };

  const editJurnal = (index) => {
    setEditIndex(index);
    setEditData(jurnalList[index]);
  };

  const tandaiDiketahui = (index) => {
    const jurnalBaruList = [...jurnalList];
    jurnalBaruList[index] = { ...jurnalBaruList[index], diketahui: true };
    setJurnalList(jurnalBaruList);
  };

  const filterJurnal = jurnalList.filter((jurnal) => {
    return (
      (filterKelas === "" || jurnal.kelas === filterKelas) &&
      (filterBulan === "" || jurnal.tanggal.slice(5, 7) === filterBulan) &&
      (filterTahun === "" || jurnal.tanggal.slice(0, 4) === filterTahun) &&
      (filterGuru === "" || jurnal.guru === filterGuru) &&
      (filterSubjek === "" || jurnal.subjek === filterSubjek)
    );
  });

  const displayedJurnal = filterJurnal.slice(
    currentPage * ITEMS_PER_PAGE,
    (currentPage + 1) * ITEMS_PER_PAGE
  );

  const eksporKeExcel = () => {
    const ws = XLSX.utils.json_to_sheet(filterJurnal);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Jurnal Kelas");
    XLSX.writeFile(wb, "Jurnal_Kelas.xlsx");
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">📖 Jurnal Kelas</h1>
      <JurnalForm tambahJurnal={tambahJurnal} editData={editData} />

      <h2 className="text-xl font-semibold mt-4">Daftar Jurnal</h2>
      {displayedJurnal.length === 0 ? (
        <p className="text-gray-500">Belum ada jurnal yang sesuai filter.</p>
      ) : (
        <table className="w-full border-collapse border border-gray-300 mt-2">
          <thead>
            <tr className="bg-gray-200">
              <th className="border p-2">Tanggal</th>
              <th className="border p-2">Kelas</th>
              <th className="border p-2">Guru</th>
              <th className="border p-2">Subjek</th>
              <th className="border p-2">Topik</th>
              <th className="border p-2">Kehadiran</th>
              <th className="border p-2">Status</th>
              <th className="border p-2">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {displayedJurnal.map((jurnal, index) => (
              <tr key={index} className="border">
                <td className="p-2">{jurnal.tanggal}</td>
                <td className="p-2">{jurnal.kelas}</td>
                <td className="p-2">{jurnal.guru}</td>
                <td className="p-2">{jurnal.subjek}</td>
                <td className="p-2">{jurnal.topik}</td>
                <td className="p-2">{jurnal.kehadiran}</td>
                <td className={`p-2 ${jurnal.diketahui ? "text-green-600" : "text-red-600"}`}>{jurnal.diketahui ? "✔ Sudah Diketahui" : "❌ Belum Diketahui"}</td>
                <td className="p-2">
                  <button className="bg-blue-500 text-white px-2 py-1 mr-2" onClick={() => editJurnal(index)}>Edit</button>
                  <button className="bg-red-500 text-white px-2 py-1" onClick={() => hapusJurnal(index)}>Hapus</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <ReactPaginate
        previousLabel={"⬅ Prev"}
        nextLabel={"Next ➡"}
        pageCount={Math.ceil(filterJurnal.length / ITEMS_PER_PAGE)}
        onPageChange={(data) => setCurrentPage(data.selected)}
        containerClassName="flex justify-center space-x-2 mt-4"
        activeClassName="font-bold"
      />
    </div>
  );
}

export default App;
