import { useState, useEffect } from "react";

function JurnalForm({ tambahJurnal, jurnalEdit }) {
  const [tanggal, setTanggal] = useState("");
  const [kelas, setKelas] = useState("");
  const [guru, setGuru] = useState("");
  const [subjek, setSubjek] = useState("");
  const [topik, setTopik] = useState("");
  const [kehadiran, setKehadiran] = useState("");
  const [keterangan, setKeterangan] = useState("");

  useEffect(() => {
    if (jurnalEdit) {
      setTanggal(jurnalEdit.tanggal);
      setKelas(jurnalEdit.kelas);
      setGuru(jurnalEdit.guru);
      setSubjek(jurnalEdit.subjek);
      setTopik(jurnalEdit.topik);
      setKehadiran(jurnalEdit.kehadiran);
      setKeterangan(jurnalEdit.keterangan);
    }
  }, [jurnalEdit]);

  const handleSubmit = (e) => {
    e.preventDefault();
    tambahJurnal({ tanggal, kelas, guru, subjek, topik, kehadiran, keterangan });
    setTanggal("");
    setKelas("");
    setGuru("");
    setSubjek("");
    setTopik("");
    setKehadiran("");
    setKeterangan("");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="date" value={tanggal} onChange={(e) => setTanggal(e.target.value)} required />
      <input type="text" placeholder="Kelas" value={kelas} onChange={(e) => setKelas(e.target.value)} required />
      <input type="text" placeholder="Nama Guru" value={guru} onChange={(e) => setGuru(e.target.value)} required />
      <input type="text" placeholder="Subjek" value={subjek} onChange={(e) => setSubjek(e.target.value)} required />
      <input type="text" placeholder="Topik" value={topik} onChange={(e) => setTopik(e.target.value)} required />
      <input type="text" placeholder="Kehadiran" value={kehadiran} onChange={(e) => setKehadiran(e.target.value)} required />
      <textarea placeholder="Keterangan" value={keterangan} onChange={(e) => setKeterangan(e.target.value)} required></textarea>
      <button type="submit">{jurnalEdit ? "Update Jurnal" : "Simpan Jurnal"}</button>
    </form>
  );
}

export default JurnalForm;
