import { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import JurnalForm from "./components/JurnalForm";

function App() {
  const [jurnalList, setJurnalList] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [editData, setEditData] = useState(null);
  const [filterKelas, setFilterKelas] = useState("");
  const [filterBulan, setFilterBulan] = useState("");
  const [filterTahun, setFilterTahun] = useState("");
  const [filterGuru, setFilterGuru] = useState("");
  const [filterSubjek, setFilterSubjek] = useState("");

  useEffect(() => {
    const dataTersimpan = localStorage.getItem("jurnalList");
    if (dataTersimpan) {
      setJurnalList(JSON.parse(dataTersimpan));
    }
  }, []);

  useEffect(() => {
    if (jurnalList.length > 0) {
      localStorage.setItem("jurnalList", JSON.stringify(jurnalList));
    } else {
      localStorage.removeItem("jurnalList");
    }
  }, [jurnalList]);

  const tambahJurnal = (jurnalBaru) => {
    jurnalBaru.diketahui = jurnalBaru.diketahui ?? false;

    if (editIndex !== null) {
      const jurnalBaruList = [...jurnalList];
      jurnalBaruList[editIndex] = jurnalBaru;
      setJurnalList(jurnalBaruList);
      setEditIndex(null);
      setEditData(null);
    } else {
      setJurnalList([...jurnalList, jurnalBaru]);
    }
  };

  const hapusJurnal = (index) => {
    if (window.confirm("Apakah Anda yakin ingin menghapus jurnal ini?")) {
      setJurnalList(jurnalList.filter((_, i) => i !== index));
    }
  };

  const editJurnal = (index) => {
    setEditIndex(index);
    setEditData(jurnalList[index]);
  };

  const tandaiDiketahui = (index) => {
    const jurnalBaruList = [...jurnalList];
    jurnalBaruList[index] = { ...jurnalBaruList[index], diketahui: true };
    setJurnalList(jurnalBaruList);
  };

  const filterJurnal = jurnalList.filter((jurnal) => {
    return (
      (filterKelas === "" || jurnal.kelas === filterKelas) &&
      (filterBulan === "" || jurnal.tanggal?.slice(5, 7) === filterBulan) &&
      (filterTahun === "" || jurnal.tanggal?.slice(0, 4) === filterTahun) &&
      (filterGuru === "" || jurnal.guru === filterGuru) &&
      (filterSubjek === "" || jurnal.subjek === filterSubjek)
    );
  });

  const eksporKeExcel = () => {
    if (filterJurnal.length === 0) {
      alert("Tidak ada data untuk diekspor.");
      return;
    }
    const ws = XLSX.utils.json_to_sheet(filterJurnal);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Jurnal Kelas");
    XLSX.writeFile(wb, "Jurnal_Kelas.xlsx");
  };

  // 🔽 Mengambil nilai unik untuk filter
  const getUniqueValues = (key) => ["", ...new Set(jurnalList.map((j) => j[key]))];

  return (
    <div style={{ padding: "20px" }}>
      <h1>📖 Jurnal Kelas</h1>
      <JurnalForm tambahJurnal={tambahJurnal} editData={editData} />

      <h2>Filter Jurnal</h2>
      <div>
        <label>Kelas: </label>
        <select value={filterKelas} onChange={(e) => setFilterKelas(e.target.value)}>
          {getUniqueValues("kelas").map((kelas) => (
            <option key={kelas} value={kelas}>
              {kelas || "Semua"}
            </option>
          ))}
        </select>

        <label>Bulan: </label>
        <select value={filterBulan} onChange={(e) => setFilterBulan(e.target.value)}>
          {[...new Set(jurnalList.map((j) => j.tanggal?.slice(5, 7)))].map((bulan) => (
            bulan && <option key={bulan} value={bulan}>{bulan}</option>
          ))}
          <option value="">Semua</option>
        </select>

        <label>Tahun: </label>
        <select value={filterTahun} onChange={(e) => setFilterTahun(e.target.value)}>
          {[...new Set(jurnalList.map((j) => j.tanggal?.slice(0, 4)))].map((tahun) => (
            tahun && <option key={tahun} value={tahun}>{tahun}</option>
          ))}
          <option value="">Semua</option>
        </select>

        <button onClick={eksporKeExcel} style={{ marginLeft: "10px", background: "green", color: "white" }}>
          Ekspor ke Excel
        </button>
      </div>

      <h2>Daftar Jurnal</h2>
      {filterJurnal.length === 0 ? (
        <p>Belum ada jurnal yang sesuai filter.</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0" style={{ width: "100%", textAlign: "left" }}>
          <thead>
            <tr>
              <th>Tanggal</th>
              <th>Kelas</th>
              <th>Guru</th>
              <th>Subjek</th>
              <th>Topik</th>
              <th>Kehadiran</th>
              <th>Keterangan</th>
              <th>Status</th>
              <th>Aksi</th>
            </tr>
          </thead>
          <tbody>
            {filterJurnal.map((jurnal, index) => (
              <tr key={index}>
                <td>{new Date(jurnal.tanggal).toLocaleDateString()}</td>
                <td>{jurnal.kelas}</td>
                <td>{jurnal.guru}</td>
                <td>{jurnal.subjek}</td>
                <td>{jurnal.topik}</td>
                <td>{jurnal.kehadiran}</td>
                <td>{jurnal.keterangan}</td>
                <td style={{ color: jurnal.diketahui ? "green" : "red" }}>
                  {jurnal.diketahui ? "✔ Sudah Diketahui" : "❌ Belum Diketahui"}
                </td>
                <td>
                  <button onClick={() => editJurnal(index)} style={{ background: "blue", color: "white" }}>Edit</button>
                  <button onClick={() => hapusJurnal(index)} style={{ background: "red", color: "white" }}>Hapus</button>
                  {!jurnal.diketahui && (
                    <button onClick={() => tandaiDiketahui(index)} style={{ background: "orange", color: "white" }}>
                      Tandai Diketahui
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
