import { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import JurnalForm from "./components/JurnalForm";

function App() {
  const [jurnalList, setJurnalList] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [editData, setEditData] = useState(null);
  const [filterKelas, setFilterKelas] = useState("");
  const [filterBulan, setFilterBulan] = useState("");
  const [filterTahun, setFilterTahun] = useState("");
  const [filterGuru, setFilterGuru] = useState("");
  const [filterSubjek, setFilterSubjek] = useState("");

  useEffect(() => {
    const dataTersimpan = localStorage.getItem("jurnalList");
    if (dataTersimpan) {
      setJurnalList(JSON.parse(dataTersimpan));
    }
  }, []);

  useEffect(() => {
    if (jurnalList.length > 0) {
      localStorage.setItem("jurnalList", JSON.stringify(jurnalList));
    } else {
      localStorage.removeItem("jurnalList");
    }
  }, [jurnalList]);

  const tambahJurnal = (jurnalBaru) => {
    jurnalBaru.diketahui = jurnalBaru.diketahui ?? false;

    if (editIndex !== null) {
      const jurnalBaruList = [...jurnalList];
      jurnalBaruList[editIndex] = jurnalBaru;
      setJurnalList(jurnalBaruList);
      setEditIndex(null);
      setEditData(null);
    } else {
      setJurnalList([...jurnalList, jurnalBaru]);
    }
  };

  const hapusJurnal = (index) => {
    if (window.confirm("Apakah Anda yakin ingin menghapus jurnal ini?")) {
      const jurnalBaru = jurnalList.filter((_, i) => i !== index);
      setJurnalList(jurnalBaru);
    }
  };

  const editJurnal = (index) => {
    setEditIndex(index);
    setEditData(jurnalList[index]);
  };

  const tandaiDiketahui = (index) => {
    const jurnalBaruList = [...jurnalList];
    jurnalBaruList[index] = { ...jurnalBaruList[index], diketahui: true };
    setJurnalList(jurnalBaruList);
  };

  const eksporKeExcel = () => {
    const ws = XLSX.utils.json_to_sheet(jurnalList);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Jurnal Kelas");
    XLSX.writeFile(wb, "Jurnal_Kelas.xlsx");
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>üìñ Jurnal Kelas</h1>
      <JurnalForm tambahJurnal={tambahJurnal} editData={editData} />

      <h2>Daftar Jurnal</h2>
      {jurnalList.length === 0 ? (
        <p>Belum ada jurnal yang tersedia.</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0" style={{ width: "100%", textAlign: "left" }}>
          <thead>
            <tr>
              <th>Tanggal</th>
              <th>Kelas</th>
              <th>Guru</th>
              <th>Subjek</th>
              <th>Topik</th>
              <th>Kehadiran</th>
              <th>Keterangan</th>
              <th>Status</th>
              <th>Aksi</th>
            </tr>
          </thead>
          <tbody>
            {jurnalList.map((jurnal, index) => (
              <tr key={index}>
                <td>{jurnal.tanggal}</td>
                <td>{jurnal.kelas}</td>
                <td>{jurnal.guru}</td>
                <td>{jurnal.subjek}</td>
                <td>{jurnal.topik}</td>
                <td>{jurnal.kehadiran}</td>
                <td>{jurnal.keterangan}</td>
                <td style={{ color: jurnal.diketahui ? "green" : "red" }}>
                  {jurnal.diketahui ? "‚úî Sudah Diketahui" : "‚ùå Belum Diketahui"}
                </td>
                <td>
                  <button onClick={() => editJurnal(index)} style={{ marginRight: "5px", background: "blue", color: "white" }}>Edit</button>
                  <button onClick={() => hapusJurnal(index)} style={{ marginRight: "5px", background: "red", color: "white" }}>Hapus</button>
                  {!jurnal.diketahui && (
                    <button onClick={() => tandaiDiketahui(index)} style={{ background: "orange", color: "white" }}>Tandai Diketahui</button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <button onClick={eksporKeExcel} style={{ background: "green", color: "white", marginTop: "10px" }}>Ekspor ke Excel</button>
    </div>
  );
}

export default App;
