import { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import JurnalForm from "./components/JurnalForm";

function App() {
  const [jurnalList, setJurnalList] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [editData, setEditData] = useState(null);
  const [filterKelas, setFilterKelas] = useState("");
  const [filterBulan, setFilterBulan] = useState("");
  const [filterTahun, setFilterTahun] = useState("");
  const [filterGuru, setFilterGuru] = useState("");
  const [filterSubjek, setFilterSubjek] = useState("");

  useEffect(() => {
    const dataTersimpan = localStorage.getItem("jurnalList");
    if (dataTersimpan) {
      setJurnalList(JSON.parse(dataTersimpan));
    }
  }, []);

  useEffect(() => {
    if (jurnalList.length > 0) {
      localStorage.setItem("jurnalList", JSON.stringify(jurnalList));
    }
  }, [jurnalList]);

  const tambahJurnal = (jurnalBaru) => {
    if (editIndex !== null) {
      const jurnalBaruList = [...jurnalList];
      jurnalBaruList[editIndex] = jurnalBaru;
      setJurnalList(jurnalBaruList);
      setEditIndex(null);
      setEditData(null);
    } else {
      setJurnalList([...jurnalList, jurnalBaru]);
    }
  };

  const hapusJurnal = (index) => {
    const jurnalBaru = jurnalList.filter((_, i) => i !== index);
    setJurnalList(jurnalBaru);
    localStorage.setItem("jurnalList", JSON.stringify(jurnalBaru));
  };

  const editJurnal = (index) => {
    setEditIndex(index);
    setEditData(jurnalList[index]);
  };

  const tandaiDiketahui = (index) => {
    const jurnalBaruList = [...jurnalList];
    jurnalBaruList[index].diketahui = true;
    setJurnalList(jurnalBaruList);
    localStorage.setItem("jurnalList", JSON.stringify(jurnalBaruList));
  };

  const isEditable = (tanggal) => {
    const sekarang = new Date();
    const tanggalJurnal = new Date(tanggal);
    const selisihWaktu = sekarang - tanggalJurnal;
    return selisihWaktu <= 24 * 60 * 60 * 1000;
  };

  const filterJurnal = jurnalList.filter((jurnal) => {
    return (
      (filterKelas === "" || jurnal.kelas === filterKelas) &&
      (filterBulan === "" || jurnal.tanggal.slice(5, 7) === filterBulan) &&
      (filterTahun === "" || jurnal.tanggal.slice(0, 4) === filterTahun) &&
      (filterGuru === "" || jurnal.guru === filterGuru) &&
      (filterSubjek === "" || jurnal.subjek === filterSubjek)
    );
  });

  return (
    <div>
      <h1>ðŸ“– Jurnal Kelas</h1>
      <JurnalForm tambahJurnal={tambahJurnal} editData={editData} />

      <h2>Filter</h2>
      <select onChange={(e) => setFilterKelas(e.target.value)}>
        <option value="">Pilih Kelas</option>
        {[...new Set(jurnalList.map((j) => j.kelas))].map((kelas) => (
          <option key={kelas} value={kelas}>{kelas}</option>
        ))}
      </select>

      <select onChange={(e) => setFilterBulan(e.target.value)}>
        <option value="">Pilih Bulan</option>
        {Array.from({ length: 12 }, (_, i) => (i + 1).toString().padStart(2, "0")).map((bulan) => (
          <option key={bulan} value={bulan}>{bulan}</option>
        ))}
      </select>

      <select onChange={(e) => setFilterTahun(e.target.value)}>
        <option value="">Pilih Tahun</option>
        {[...new Set(jurnalList.map((j) => j.tanggal.slice(0, 4)))].map((tahun) => (
          <option key={tahun} value={tahun}>{tahun}</option>
        ))}
      </select>

      <select onChange={(e) => setFilterGuru(e.target.value)}>
        <option value="">Pilih Guru</option>
        {[...new Set(jurnalList.map((j) => j.guru))].map((guru) => (
          <option key={guru} value={guru}>{guru}</option>
        ))}
      </select>

      <select onChange={(e) => setFilterSubjek(e.target.value)}>
        <option value="">Pilih Subjek</option>
        {[...new Set(jurnalList.map((j) => j.subjek))].map((subjek) => (
          <option key={subjek} value={subjek}>{subjek}</option>
        ))}
      </select>

      <h2>Daftar Jurnal</h2>
      <table>
        <thead>
          <tr>
            <th>Tanggal</th>
            <th>Kelas</th>
            <th>Guru</th>
            <th>Subjek</th>
            <th>Aksi</th>
          </tr>
        </thead>
        <tbody>
          {filterJurnal.map((jurnal, index) => (
            <tr key={index}>
              <td>{jurnal.tanggal}</td>
              <td>{jurnal.kelas}</td>
              <td>{jurnal.guru}</td>
              <td>{jurnal.subjek}</td>
              <td>
                <button onClick={() => editJurnal(index)} disabled={!isEditable(jurnal.tanggal)}>Edit</button>
                <button onClick={() => hapusJurnal(index)}>Hapus</button>
                <button onClick={() => tandaiDiketahui(index)}>{jurnal.diketahui ? "âœ” Diketahui" : "Tandai Diketahui"}</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
