import { useState, useEffect } from "react";

function JurnalForm({ tambahJurnal, editData }) {
  const [formData, setFormData] = useState({
    tanggal: "",
    kelas: "",
    guru: "",
    subjek: "",
    topik: "",
    kehadiran: "",
    keterangan: ""
  });

  useEffect(() => {
    if (editData) {
      setFormData(editData);
    }
  }, [editData]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    tambahJurnal(formData);
    setFormData({ tanggal: "", kelas: "", guru: "", subjek: "", topik: "", kehadiran: "", keterangan: "" });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="date" name="tanggal" value={formData.tanggal} onChange={handleChange} required />
      <input type="text" name="kelas" placeholder="Kelas" value={formData.kelas} onChange={handleChange} required />
      <input type="text" name="guru" placeholder="Nama Guru" value={formData.guru} onChange={handleChange} required />
      <input type="text" name="subjek" placeholder="Subjek" value={formData.subjek} onChange={handleChange} required />
      <input type="text" name="topik" placeholder="Topik" value={formData.topik} onChange={handleChange} required />
      <input type="text" name="kehadiran" placeholder="Kehadiran" value={formData.kehadiran} onChange={handleChange} required />
      <input type="text" name="keterangan" placeholder="Keterangan" value={formData.keterangan} onChange={handleChange} required />
      <button type="submit">{editData ? "Update Jurnal" : "Tambah Jurnal"}</button>
    </form>
  );
}

export default JurnalForm;
