import { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import JurnalForm from "./components/JurnalForm";

function App() {
  const [jurnalList, setJurnalList] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [editData, setEditData] = useState(null);
  const [filterKelas, setFilterKelas] = useState("");
  const [filterBulan, setFilterBulan] = useState("");
  const [filterTahun, setFilterTahun] = useState("");
  const [filterTanggal, setFilterTanggal] = useState("");
  const [filterGuru, setFilterGuru] = useState("");
  const [filterSubjek, setFilterSubjek] = useState("");
  const [filterTopik, setFilterTopik] = useState("");

  useEffect(() => {
    const dataTersimpan = localStorage.getItem("jurnalList");
    if (dataTersimpan) {
      setJurnalList(JSON.parse(dataTersimpan));
    }
  }, []);

  useEffect(() => {
    if (jurnalList.length > 0) {
      localStorage.setItem("jurnalList", JSON.stringify(jurnalList));
    } else {
      localStorage.removeItem("jurnalList");
    }
  }, [jurnalList]);

  const tambahJurnal = (jurnalBaru) => {
    jurnalBaru.dibuatPada = new Date().getTime();
    jurnalBaru.diketahui = jurnalBaru.diketahui ?? false;
    setJurnalList([...jurnalList, jurnalBaru]);
  };

  const hapusJurnal = (index) => {
    if (window.confirm("Apakah Anda yakin ingin menghapus jurnal ini?")) {
      const jurnalBaru = jurnalList.filter((_, i) => i !== index);
      setJurnalList(jurnalBaru);
    }
  };

  const editJurnal = (index) => {
    const now = new Date().getTime();
    const createdTime = jurnalList[index].dibuatPada;
    if (now - createdTime <= 24 * 60 * 60 * 1000) {
      setEditIndex(index);
      setEditData(jurnalList[index]);
    } else {
      alert("Edit hanya bisa dilakukan dalam 24 jam setelah dibuat.");
    }
  };

  const tandaiDiketahui = (index) => {
    const jurnalBaruList = [...jurnalList];
    jurnalBaruList[index] = { ...jurnalBaruList[index], diketahui: true };
    setJurnalList(jurnalBaruList);
  };

  const filterJurnal = jurnalList.filter((jurnal) => {
    return (
      (filterKelas === "" || jurnal.kelas === filterKelas) &&
      (filterBulan === "" || jurnal.tanggal.slice(5, 7) === filterBulan) &&
      (filterTahun === "" || jurnal.tanggal.slice(0, 4) === filterTahun) &&
      (filterTanggal === "" || jurnal.tanggal === filterTanggal) &&
      (filterGuru === "" || jurnal.guru === filterGuru) &&
      (filterSubjek === "" || jurnal.subjek === filterSubjek) &&
      (filterTopik === "" || jurnal.topik.toLowerCase().includes(filterTopik.toLowerCase()))
    );
  });

  const eksporKeExcel = () => {
    const ws = XLSX.utils.json_to_sheet(filterJurnal);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Jurnal Kelas");
    XLSX.writeFile(wb, "Jurnal_Kelas.xlsx");
  };

  return (
    <div>
      <h1>📖 Jurnal Kelas</h1>
      <JurnalForm tambahJurnal={tambahJurnal} editData={editData} />

      <h2>Filter Jurnal</h2>
      <div>
        <label>Tanggal: </label>
        <input type="date" value={filterTanggal} onChange={(e) => setFilterTanggal(e.target.value)} />
        <label>Bulan: </label>
        <input type="month" value={filterBulan} onChange={(e) => setFilterBulan(e.target.value)} />
        <label>Tahun: </label>
        <input type="number" placeholder="YYYY" value={filterTahun} onChange={(e) => setFilterTahun(e.target.value)} />
        <button onClick={eksporKeExcel} style={{ marginLeft: "10px", background: "green", color: "white" }}>Ekspor ke Excel</button>
      </div>

      <h2>Daftar Jurnal</h2>
      {filterJurnal.length === 0 ? (
        <p>Belum ada jurnal yang sesuai filter.</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0" style={{ width: "100%", textAlign: "left" }}>
          <thead>
            <tr>
              <th>Tanggal</th>
              <th>Kelas</th>
              <th>Guru</th>
              <th>Subjek</th>
              <th>Topik</th>
              <th>Status</th>
              <th>Aksi</th>
            </tr>
          </thead>
          <tbody>
            {filterJurnal.map((jurnal, index) => (
              <tr key={index}>
                <td>{jurnal.tanggal}</td>
                <td>{jurnal.kelas}</td>
                <td>{jurnal.guru}</td>
                <td>{jurnal.subjek}</td>
                <td>{jurnal.topik}</td>
                <td style={{ color: jurnal.diketahui ? "green" : "red" }}>
                  {jurnal.diketahui ? "✔ Sudah Diketahui" : "❌ Belum Diketahui"}
                </td>
                <td>
                  <button onClick={() => editJurnal(index)} style={{ marginRight: "5px", background: "blue", color: "white" }}>Edit</button>
                  <button onClick={() => hapusJurnal(index)} style={{ marginRight: "5px", background: "red", color: "white" }}>Hapus</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
