import { useState, useEffect } from "react";
import JurnalForm from "./components/JurnalForm";

function App() {
  const [jurnalList, setJurnalList] = useState([]);

  // Load data dari Local Storage saat pertama kali aplikasi dibuka
  useEffect(() => {
    const dataTersimpan = localStorage.getItem("jurnalList");
    if (dataTersimpan) {
      setJurnalList(JSON.parse(dataTersimpan));
    }
  }, []);

  // Simpan ke Local Storage setiap kali daftar jurnal berubah
  useEffect(() => {
    if (jurnalList.length > 0) {
      localStorage.setItem("jurnalList", JSON.stringify(jurnalList));
    }
  }, [jurnalList]);

  const tambahJurnal = (jurnalBaru) => {
    setJurnalList([...jurnalList, jurnalBaru]);
  };

  return (
    <div>
      <h1>ðŸ“– Jurnal Kelas</h1>
      <JurnalForm tambahJurnal={tambahJurnal} />

      <h2>Daftar Jurnal</h2>
      {jurnalList.length === 0 ? (
        <p>Belum ada jurnal.</p>
      ) : (
        <ul>
          {jurnalList.map((jurnal, index) => (
            <li key={index}>
              <strong>{jurnal.tanggal}</strong> - {jurnal.kelas} - {jurnal.guru} - {jurnal.subjek} - {jurnal.topik} - Kehadiran: {jurnal.kehadiran} - {jurnal.keterangan}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
